from PyQt5.QtWidgets import QPushButton
from PyQt5 import QtGui, QtWidgets
from PyQt5.QtCore import QSettings, Qt
from licensa import *
import base64
import sys


icon_base64 = ""

class LicenseWindow(QtWidgets.QWidget):
    def __init__(self):
        super().__init__()
        self.setWindowTitle("Python SaS")
        self.setFixedSize(300, 140)
        #icone        
        icon_data = base64.b64decode(icon_base64)
        image_icon = QtGui.QImage.fromData(icon_data)
        icon = QtGui.QIcon(QtGui.QPixmap.fromImage(image_icon))
        self.setWindowIcon(icon)
        settings = QSettings("settings.ini", QSettings.IniFormat)
        self.textbox_state = settings.value("serial", "", type=str)

        # Criação dos widgets
        label_link = QtWidgets.QLabel(self)
        label_link.setOpenExternalLinks(True)
        label_link.setTextFormat(Qt.RichText)
        label_link.setText('<a href="https://example.com/">Adquira sua licença</a>')
        label = QtWidgets.QLabel("Insira o código de licença:")
        self.textbox = QtWidgets.QLineEdit()
        self.textbox.setText(self.textbox_state)  # define a opção selecionada
        self.textbox.textChanged.connect(self.salvar_opcoes)  # conecta o sinal currentIndexChanged para salvar as opções
        button = QPushButton("Confirmar")

        # Configuração do layout
        layout = QtWidgets.QVBoxLayout()
        layout.addWidget(label_link)
        layout.addWidget(label)
        layout.addWidget(self.textbox)
        layout.addWidget(button)
        self.setLayout(layout)

        # Variável de instância para a janela principal
        self.main_window = None

        # Conexão do botão de confirmação com o método de validação
        button.clicked.connect(self.validate_license)
        
    def salvar_opcoes(self):
        settings = QSettings("settings.ini", QSettings.IniFormat)
        self.textbox_state = self.textbox.text()
        settings.setValue("serial", self.textbox_state)

    def validate_license(self):
        
        # Verificação do código de licença
        license_code = self.textbox.text()
        key = str(self.textbox.text())
        success, expiration_date = Authkey(key)
        if success:
            expiration_date_formatted = expiration_date.strftime('%d-%m-%Y %H:%M:%S')
            #print(expiration_date_formatted)
            QtWidgets.QMessageBox.information(self, "Python SaS", "Sucesso !\nA licença inserida é válida.\nIrá expirar: " + expiration_date_formatted)
            # Código de licença válido, fechar a janela de licença e abrir a janela principal
            self.main_window = MainWindow()
            self.main_window.showMaximized()
            self.close()
        else:
            QtWidgets.QMessageBox.critical(self, "Python SaS", "Erro de Autenticação\nA licença inserida é inválida.")
            

class MainWindow(QtWidgets.QMainWindow):
    def __init__(self):
        super().__init__()
        self.setWindowTitle("Interface Principal")
        self.setGeometry(100, 100, 400, 300)

        # Criar um widget central e definir o layout
        central_widget = QtWidgets.QWidget(self)
        layout = QtWidgets.QVBoxLayout(central_widget)
        
        # Adicionar o label ao layout do widget central
        label = QtWidgets.QLabel("Aqui Está Sua Interface")
        layout.addWidget(label)

        # Definir o widget central da QMainWindow
        self.setCentralWidget(central_widget)

        
if __name__ == "__main__":
    app = QtWidgets.QApplication([])

    # Criação da janela de licença
    license_window = LicenseWindow()
    license_window.show()

    sys.exit(app.exec_())